<?phpclass groupController{		public function showGroup()	{		if (isset($_GET['gp']))		{		$gp = intval ($_GET['gp']);		}		else 		{			$gp=0;		}		$group = new GroupModel();        $group->load($gp);		$group->getRelatedGroup();		$group->menuFotoProductÑurrentGroup();				$view = new GroupView();        $view->set($group);        $view->renderHtml();	}}